import React, { useState } from 'react';
import Note from '../../modules/Note';
import { useAllNotes } from '../../contexts/notes';


interface NoteBoxProps {
    note: Note;
}

const NoteBox: React.FC<NoteBoxProps> = ({ note }) => {


    return (
        <div className='w-60 h-40 bg-white border border-black rounded'>
            <h2 className='text-xl'>{note.title}</h2>
            <p>{note.description}</p>

            <button className='bg-red-500'>Supprimer</button>
            <button className='bg-yellow-500'>Marquer comme importante</button>
        </div>
    );
};

export default NoteBox;
